='''' link='''' target='''' caption='''' font_size='''' appearance='''' overlay_opacity=''0.4'' overlay_color=''#000000'' overlay_text_color=''#ffffff''][/av_image]\r\n\r\n[av_textblock size=''12'' font_color=''custom'' color=''#666666'']\r\n<p style="margin-left: 10px;">麦觉理角开发项目</p>\r\n[/av_textblock]\r\n\r\n[/av_one_fourth][av_one_fourth min_height='''' vertical_alignment=''av-align-top'' space='''' custom_margin=''aviaTBcustom_margin'' margin=''0px,20px'' padding=''0px'' padding_sync=''true'' border='''' border_color='''' radius=''5px'' radius_sync=''true'' background_color=''#ffffff'' src='''' attachment='''' attachment_size='''' background_position=''top left'' background_repeat=''no-repeat'']\r\n\r\n[av_image src=''http://localhost/tasmania/wp-content/uploads/2015/08/tas_invest_pic04.png'' attachment=''353'' attachment_size=''full'' align=''center'' animation=''no-animation'' styling='''' hover='''' link='''' target='''' caption='''' font_size='''' appearance='''' overlay_opacity=''0.4'' overlay_color=''#000000'' overlay_text_color=''#ffffff''][/av_image]\r\n\r\n[av_textblock size=''12'' font_color=''custom'' color=''#666666'']\r\n<p style="margin-left: 10px;">世界遗产地区和国家公园保护区旅游投资</p>\r\n[/av_textblock]\r\n\r\n[/av_one_fourth][av_one_fourth first min_height='''' vertical_alignment=''av-align-top'' space='''' custom_margin=''aviaTBcustom_margin'' margin=''0px,20px'' padding=''0px'' padding_sync=''true'' border='''' border_color='''' radius=''5px'' radius_sync=''true'' background_color=''#ffffff'' src='''' attachment='''' attachment_size='''' background_position=''top left'' background_repeat=''no-repeat'']\r\n\r\n[av_image src=''http://localhost/tasmania/wp-content/uploads/2015/08/tas_invest_pic05.png'' attachment=''354'' attachment_size=''full'' align=''center'' animation=''no-animation'' styling='''' hover='''' link='''' target='''' caption='''' font_size='''' appearance='''' overlay_opacity=''0.4'' overlay_color=''#000000'' overlay_text_color=''#ffffff''][/av_