t='''' attachment_size='''' background_position=''top left'' background_repeat=''no-repeat'']\r\n\r\n[av_image src=''http://localhost/tasmania/wp-content/uploads/2015/09/tas_StudyAbroad_pic09.png'' attachment=''448'' attachment_size=''full'' align=''center'' animation=''no-animation'' styling='''' hover='''' link='''' target='''' caption='''' font_size='''' appearance='''' overlay_opacity=''0.4'' overlay_color=''#000000'' overlay_text_color=''#ffffff''][/av_image]\r\n\r\n[av_textblock size=''12'' font_color=''custom'' color=''#666666'']\r\n<p style="margin-left: 10px; width: 100%;">中国专科毕业生读塔大硕士<span style="float: right; color: #c9161c; margin-right: 20px;">$1300起</span></p>\r\n[/av_textblock]\r\n\r\n[/av_one_fourth][av_one_fourth min_height='''' vertical_alignment=''av-align-top'' space='''' custom_margin=''aviaTBcustom_margin'' margin=''0px,20px'' padding=''0px'' padding_sync=''true'' border=''1'' border_color=''#999999'' radius=''5px'' radius_sync=''true'' background_color=''#ffffff'' src='''' attachment='''' attachment_size='''' background_position=''top left'' background_repeat=''no-repeat'']\r\n\r\n[av_image src=''http://localhost/tasmania/wp-content/uploads/2015/09/tas_StudyAbroad_pic10.png'' attachment=''449'' attachment_size=''full'' align=''center'' animation=''no-animation'' styling='''' hover='''' link='''' target='''' caption='''' font_size='''' appearance='''' overlay_opacity=''0.4'' overlay_color=''#000000'' overlay_text_color=''#ffffff''][/av_image]\r\n\r\n[av_textblock size=''12'' font_color=''custom'' color=''#666666'']\r\n<p style="margin-left: 10px; width: 100%;">中国本科毕业生读塔大硕士<span style="float: right; color: #c9161c; margin-right: 20px;">$1000起</span></p>\r\n[/av_textblock]\r\n\r\n[/av_one_fourth][av_one_fourth min_height='''' vertical_alignment=''av-align-top'' space='''' custom_margin=''aviaTBcustom_margin'' margin=''0px,20px'' padding=''0px'' padding_sync=''true'' border=''1'' border_color=''#999999'' radius=''5px'' radius_sync=''true'' background_color=''#ffffff'' src='''' attachment='''' attachment_size='''' background_position=''top left'' background_repeat=''no-repeat'']\r\n\r\n[av_image src=''http://localhost/tasmania/wp-content/uploads/2015/09/tas_StudyAbroad_pic11.png'' attachment=''450'' attachment_size=''full'' align=''center'' animation=''no-animation'' styling='''' hover='''' link='''' target='''' caption='''' font_size='''' appearance='''' overlay_opacity=''0.4'' overlay_color=''#000000'' overlay_text_color=''#ffffff''][/av_image]\r\n\r\n[av_textblock size=''12'' font_color=''custom'' color=''#666666'']\r\n<p style="margin-left: 10px; width: 100%;">澳洲大陆留学生转学塔大<span style="float: right; color: #c9161c; margin-right: 20px;">服务费$0起</span></p>\r\n[/av_textblock]\r\n\r\n[/av_one_fourth][av_one_fourth min_height='''' vertical_alignment=''av-align-top'' space='''' custom_margin=''aviaTBcustom_margin'' margin=''0px,20px'' padding=''0px'' padding_sync=''true'' border=''1'' border_color=''#999999'' radius=''5px'' radius_sync=''true'' background_color=''#ffffff'' src='''' attachment='''' attachment_size='''' background_position=''top left'' background_repeat=''no-repeat'']\r\n\r\n[av_image src=''http://localhost/tasmania/wp-content/uploads/2015/09/tas_StudyAbroad_pic12.png'' attachment=''451'' attachment_size=''full'' align=''center'' animation=''no-animation'' styling='''' hover='''' link='''' target='''' caption='''' font_size='''' appearance='''' overlay_opacity=''0.4'' overlay_color=''#000000'' overlay_text_color=''#ffffff''][/av_image]\r\n\r\n[av_textblock size=''12'' font_color=''custom'' color=''#666666'']\r\n<p style="margin-left: 10px; width: 100%;">澳洲大陆留学生，\r\n转学塔大到移民一条龙服务<span style="float: right; color: #c9161c; margin-right: 20px;">$1000起+GST</span></p>\r\n[/av_textblock]\r\n\r\n[/av_one_fourth][/av_cell_one_full][/av_layout_row][av_layout_row border='''' min_height=''0'' color=''main_color'' mobile=''av-flex-cells'' id='''']\r\n[av_cell_one_full vertical_align=''top'' padding=''10px,0px,10px,0px'' background_color='''' src='''' attachment='''' attachment_size='''' background_attachment=''scroll'' background_position=''top left'' background_repeat=''no-repeat'']\r\n\r\n[/av_cell_one_full]\r\n[/av_layout_row]\r\n\r\n[av_layout_row border='''' min_height=''0'' color=''main_color'' mobile=''av-flex-cells'' id=''content_block'']\r\n\r\n[av_cell_one_full vertical_align=''top'' padding=''0px,20px,0px,20px'' background_color=''#f7f7f7'' src='''' attachment='''' attachment_size='''' background_attachment=''scroll'' background_position=''top left'' background_repeat=''no-repeat'']\r\n\r\n[av_codeblock wrapper_element='''' wrapper_element_attributes='''']\r\n<div class="subject-title">\r\n<h3>塔斯马尼亚TAFE</h3>\r\n</div>\r\n[/av_codeblock]\r\n\r\n[av_textblock size=''14'' font_color=''custom'' color=''#666666'']\r\n完成了塔斯马尼亚TAFE的文凭和高级文凭课程，可以升读塔斯马尼亚大学二年级。文凭或高级文凭的课程种类超过了40个，有商科、金融、会计、酒店管理和旅游、社区服务、儿童服务、园艺、平面设计、实验室技术、艺术和音乐等课程都是很适合低学历学生就读。起点比大学低，学费比大学便宜，课程的实践性强，获得文凭后容易进入职场等特点吸引了很多海外留学生通过在TAFE的学习后再进入塔斯马尼亚大学进行学习。\r\n\r\n塔斯马尼亚TAFE还是塔斯马尼亚州唯一的公立技能教育的学院，共有13个校区，开设了汽修、建筑、电工、采矿、工程、美容美发、老年护理、钣金工等课程，为塔斯马尼亚和澳大利亚其他地区，每年输送数万名高质量的技能型人才，毕业学生的就业率和升学率超过90%。\r\n\r\n和就读塔斯马尼亚大学的留学生一样，塔斯马尼亚州政府也为在塔斯马尼亚TAFE完成学业的学生提供移民的担保，学生也一样可以获得在偏于地区学习的加分，同时对技能类的学生，雇主担保移民机会也很多。\r\n[/av_textblock]\r\n\r\n[av_one_fourth first min_height='''' vertical_alignment=''av-align-top'' space='''' custom_margin=''aviaTBcustom_margin'' margin=''0px,20px'' padding=''0px'' padding_sync=''true'' border=''1'' border_color=''#999999'' radius=''5px'' radius_sync=''true'' background_color=''#ffffff'' src='''' attachment='''' attachment_size='''' background_position=''top left'' background_repeat=''no-repeat'']\r\n\r\n[av_image src=''http://localhost/tasmania/wp-content/uploads/2015/09/tas_StudyAbroad_pic13.png'' attachment=''452'' attachment_size=''full'' align=''center'' animation=''no-animation'' styling='''' hover='''' link='''' target='''' caption='''' font_size='''' appearance='''' overlay_opacity=''0.4'' overlay_color=''#000000'' overlay_text_color=''#ffffff''][/av_image]\r\n\r\n[av_textblock size=''12'' font_color=''custom'' color=''#666666'']\r\n<p style="margin-left: 10px; width: 100%;">中国高中毕业生读塔州TAFE和签证<span style="float: right; color: #c9161c; margin-right: 20px;">$1500起</span></p>\r\n[/av_textblock]\r\n\r\n[/av_one_fourth][av_one_fourth min_height='''' vertical_alignment=''av-align-top'' space='''' custom_margin=''aviaTBcustom_margin'' margin=''0px,20px'' padding=''0px'' padding_sync=''true'' border=''1'' border_color=''#999999'' radius=''5px'' radius_sync=''true'' background_color=''#ffffff'' src='''' attachment='''' attachment_size='''' background_position=''top left'' background_repeat=''no-repeat'']\r\n\r\n[av_image src=''http://localhost/tasmania/wp-content/uploads/2015/09/tas_StudyAbroad_pic14.png'' attachment=''453'' attachment_size=''full'' align=''center'' animation=''no-animation'' styling='''' hover='''' link='''' target='''' caption='''' font_size='''' appearance='''' overlay_opacity=''0.4'' overlay_color=''#000000'' overlay_text_color=''#ffffff''][/av_image]\r\n\r\n[av_textblock size=''12'' font_color=''custom'' color=''#666666'']\r\n<p style="margin-left: 10px; width: 100%;">中国高中毕业生\r\n读塔州TAFE+本科和签证<span style="float: right; color: #c9161c; margin-right: 20px;">$1250起</span></p>\r\n[/av_textblock]\r\n\r\n[/av_one_fourth][/av_cell_one_full][/av_layout_row][av_layout_row border='''' min_height=''0'' color=''main_color'' mobile=''av-flex-cells'' id='''']\r\n\r\n[av_cell_one_full vertical_align=''top'' padding=''20px,0px,20px,0px'' background_color='''' src='''' attachment='''' attachment_size='''' background_attachment=''scroll'' background_position=''top left'' background_repeat=''no-repeat'']\r\n\r\n[av_one_third first min_height='''' vertical_alignment=''av-align-top'' space=''no_margin'' margin=''0px'' margin_sync=''true'' padding=''0px'' padding_sync=''true'' border='''' border_color='''' radius=''5px'' radius_sync=''true'' background_color='''' src='''' attachm